import {React, Fragment, useState} from 'react';

const EditCard = ({ card }) => {
    const [cardTitle, setCardTitle] = useState('');

    const editTodo = async() => {
        try {
            const body= {cardTitle};
            const res = await fetch(`http://localhost:5000/todos/card/${card.card_id}`, {
                method: "PUT",
                headers: {
                    "Content-Type": "application/json"
                },
                body: JSON.stringify(body)
            })

            //refresh page
            console.log(res);
        } catch (error) {
            console.error(error.message);
        }
    }

    return (
        <Fragment>
            <button type="button" class="btn btn-primary" data-toggle="modal" data-target={`#id${card.card_id}`}>
            Rename
            </button>

            <div class="modal fade" id={`id${card.card_id}`} tabindex="-1" role="dialog" aria-labelledby="exampleModalLabel" aria-hidden="true">
            <div class="modal-dialog" role="document">
                <div class="modal-content">
                <div class="modal-header">
                    <h5 class="modal-title" id="exampleModalLabel">Rename Card</h5>
                </div>
                <div class="modal-body">
                    <input type="text" placeholder={card.card_name} value={cardTitle} onChange={e => setCardTitle(e.target.value)}></input>
                </div>
                <div class="modal-footer">
                    <button type="button" class="btn btn-secondary" data-dismiss="modal">Close</button>
                    <button type="button" class="btn btn-primary" onClick={editTodo}>Save changes</button>
                </div>
                </div>
            </div>
            </div>
        </Fragment>
    );
}

export default EditCard;
