import React, { Fragment } from 'react';
import EditCard from './EditCard';
import {ReactComponent as Delete} from '../imgs/x-mark.svg';
import {ReactComponent as View} from '../imgs/view.svg';

const CardList = ({ setCardList, cardList, deleteCard, viewCard, allTodos }) => {
    if(cardList.length > 0) {
        return (
            <Fragment>
                {cardList.map((card) => {
                    const cardTodos = allTodos.filter(t => t.card_id === card.card_id && t.todo_title !== null);
                    return <div className="card" id={card.card_id} key={card.card_id} >
                                <div className="card-title-wrap">
                                    <span className="card-title">{card.card_name}</span>
                                    <span className="task-num">{cardTodos.length} Tasks</span>
                                </div>
                                <div className="button-wrap">
                                    <EditCard setCardList={setCardList} cardList={cardList} card={card}/>
                                    <View onClick={viewCard}/>
                                    <Delete onClick={deleteCard} />
                                </div>
                            </div>
                })}
            </Fragment>
        )
    } else {
        return (
            <div>No cards</div>
        )
    }
}

export default CardList;
