import React, { Fragment, useEffect, useState } from 'react';
import CardList from './CardList';
import Card from './Card';

const Todos = ({ setAuth }) => {
    const [cardName, setCardName] = useState('');
    const [cardList, setCardList] = useState([]);
    const [todoName, setTodoName] = useState('');
    const [mainCard, setMainCard] = useState({});
    const [mainTodos, setMainTodos] = useState([]); 

    const fetchData = async() => {
        try {
            const res = await fetch("http://localhost:5000/todos", {
                method: "GET",
                headers: { token: localStorage.token}
            });

            const parseRes = await res.json();
            console.log(parseRes);
            const cards = parseRes.filter(card => card.card_id);
            console.log(cards);
            setCardList(cards);
            setMainCard(cards[0]);

            const todosMain = parseRes.filter(t => t.card_id === cards[0].card_id && t.todo_title !== null);
            setMainTodos(todosMain);
        } catch (error) {
            console.error(error.message);
        }
    }

    useEffect(()=> {
        fetchData();
    }, []);

    const logout = (e) => {
        e.preventDefault()
        localStorage.removeItem('token');
        setAuth(false);
    }

    const createCard = async(e) => {
        e.preventDefault();
        const jsonHead = new Headers();
        jsonHead.append('Content-type', 'application/json');
        jsonHead.append('token', localStorage.token);

        const body = {cardName};

        try {
            const res = await fetch("http://localhost:5000/todos/card", {
                method: "POST",
                headers: jsonHead,
                body: JSON.stringify(body)
            });

            const parseRes = await res.json();
            setCardList([...cardList, parseRes[0]]);
            setCardName('');
        } catch (error) {
            console.error(error.message);
        }
    }

    const deleteCard = async(e) => {
        e.preventDefault();
        const jsonHead = new Headers();
        jsonHead.append('Content-type', 'application/json');
        jsonHead.append('token', localStorage.token);
        const cardId = e.target.parentElement.id;
        const body = {cardId};

        try {
            // eslint-disable-next-line
            const res = await fetch("http://localhost:5000/todos/card", {
                method: "DELETE",
                headers: jsonHead,
                body: JSON.stringify(body)
            });

            const parseRes = await res.json();
            const deleteCard = cardList.filter( c => c.card_id !== parseInt(cardId));
            setCardList(deleteCard);
        } catch (error) {
            console.error(error.message);
        }
    }

    const createTodo = async(e) => {
        e.preventDefault();
        const jsonHead = new Headers();
        jsonHead.append('Content-type', 'application/json');
        jsonHead.append('token', localStorage.token);
        const cardId = mainCard.card_id;
        const body = {todoName, cardId};

        try {
            const res = await fetch("http://localhost:5000/todos/todo", {
                method: "POST",
                headers: jsonHead,
                body: JSON.stringify(body)
            });

            const parseRes = await res.json();
            console.log(parseRes);
        } catch (error) {
            console.error(error.message);
        }
    }

    return (
        <Fragment>
            <Card setTodoName={setTodoName} todoName={todoName} createTodo={createTodo} mainCard={mainCard} mainTodos={mainTodos}/>
            <CardList cardList={cardList} deleteCard={deleteCard}/>
            <form onSubmit={createCard}>
                <input value={cardName} type="text" name="card" placeholder="Card title" onChange={e => setCardName(e.target.value)}></input>
                <button>Create Card</button>
            </form>

            <button onClick={logout}>Logout</button>
        </Fragment>
    );
}

export default Todos;
