import React from 'react';
import EditTodo from './EditTodo';
import '../index';

const Card = ({ createTodo, deleteTodo, todoName, setTodoName, mainTodos, mainCard }) => {

    if(mainTodos.length === 0) {
        return (
            <div id="card-wrap">
                <div id="card-title">{mainCard.card_name}</div>
                Add a todo
                <form onSubmit={createTodo}>
                    <input value={todoName} type="text" name="todo" placeholder="Todo description" onChange={e => setTodoName(e.target.value)}></input>
                    <button>Create Todo</button>
                </form>
            </div>
        )
    } else {
        return (
            <div id="card-wrap">
                <div id="card-title">{mainCard.card_name}</div>
                <ul>
                {mainTodos.map((todo) => {
                    return <li id={todo.todo_id} key={todo.todo_id}>{todo.todo_title}<button onClick={deleteTodo}>Delete Todo</button><EditTodo todo={todo}/></li>
                })}
                </ul>

                <form onSubmit={createTodo}>
                    <input value={todoName} type="text" name="todo" placeholder="Todo description" onChange={e => setTodoName(e.target.value)}></input>
                    <button>Create Todo</button>
                </form>
            </div>
        )
    }

}

export default Card;
