import React, { Fragment, useEffect, useState } from 'react';

const Todos = ({ setAuth }) => {
    const [cardName, setCardName] = useState('');
    const [todoName, setTodoName] = useState('');

    const fetchData = async() => {
        try {
            const res = await fetch("http://localhost:5000/todos", {
                method: "GET",
                headers: { token: localStorage.token}
            });

            const parseRes = await res.json();
            console.log(parseRes);
        } catch (error) {
            console.error(error.message);
        }
    }

    useEffect(()=> {
        fetchData();
    }, []);

    const logout = (e) => {
        e.preventDefault()
        localStorage.removeItem('token');
        setAuth(false);
    }

    const createCard = async(e) => {
        e.preventDefault();
        const jsonHead = new Headers();
        jsonHead.append('Content-type', 'application/json');
        jsonHead.append('token', localStorage.token);

        const body = {cardName};

        try {
            const res = await fetch("http://localhost:5000/todos/card", {
                method: "POST",
                headers: jsonHead,
                body: JSON.stringify(body)
            });

            const parseRes = await res.json();
        } catch (error) {
            console.error(error.message);
        }
    }

    return (
        <Fragment>
            {/*<Card />
            <CardList />*/}
            <form onSubmit={createCard}>
                <input type="text" name="card" placeholder="Card title" onChange={e => setCardName(e.target.value)}></input>
                <button>Create Card</button>
            </form>

