                <input type="text" name="todo" placeholder="Todo description" onChange={e => setTodoName(e.target.value)}></input>
                <button>Create Todo</button>
            </form>

            <button onClick={logout}>Logout</button>
        </Fragment>
    );
}

export default Todos;
