import React, { useEffect, useState, Fragment } from 'react';
import CardList from './CardList';
import Card from './Card';
import '../styles/todo.css';
import {ReactComponent as Delete} from '../imgs/x-mark.svg';

const Todos = ({ setAuth }) => {
    const [cardName, setCardName] = useState('');
    const [cardList, setCardList] = useState([]);
    const [todoName, setTodoName] = useState('');
    const [mainCard, setMainCard] = useState({});
    const [mainTodos, setMainTodos] = useState([]); 
    const [allTodos, setAllTodos] = useState([]);
    const [viewTodos, setViewTodos] = useState(false);
    const [username, setUsername] = useState('');
    const [allCards, setAllCards] = useState([]);

    const fetchData = async() => {
        try {
            const res = await fetch("http://localhost:5000/todos", {
                method: "GET",
                headers: { token: localStorage.token}
            });

            const [cards, todos] = await res.json();

            setAllTodos(todos);

            const listCards = cards.filter(c => c.card_id !== null);

            if(listCards.length > 0) {
                setCardList(listCards);
                setMainCard(listCards[0]);

                for(let i = 0; i < listCards.length; i++) {
                    listCards[i].todos = todos.filter(t => t.card_id === listCards[i].card_id && t.todo_title !== null);
                }
                setAllCards(listCards);
                console.log(listCards);
            }

            const todosMain = todos.filter(t => t.card_id === cards[0].card_id && t.todo_title !== null);
            setMainTodos(todosMain);
            setUsername(cards[0].user_name);

        } catch (error) {
            console.error(error.message);
        }
    }

    useEffect(()=> {
        fetchData();
    }, []);

    const logout = (e) => {
        e.preventDefault()
        localStorage.removeItem('token');
        setAuth(false);
    }

    const createCard = async(e) => {
        e.preventDefault();

        if(cardName === '') {
            return;
        }

        const jsonHead = new Headers();
        jsonHead.append('Content-type', 'application/json');
        jsonHead.append('token', localStorage.token);

        const body = {cardName};

        try {
            const res = await fetch("http://localhost:5000/todos/card", {
                method: "POST",
                headers: jsonHead,
                body: JSON.stringify(body)
            });

            let parseRes = await res.json();
            parseRes[0].todos = [];

            setAllCards([...allCards, parseRes[0]]);
            setCardName('');
        } catch (error) {
            console.error(error.message);
        }
    }

    const deleteCard = async(e) => {
        e.stopPropagation();
        e.preventDefault();

        const jsonHead = new Headers();
        jsonHead.append('Content-type', 'application/json');
        jsonHead.append('token', localStorage.token);
        const cardId = e.currentTarget.parentElement.parentElement.id;
        const body = {cardId};

        try {
            // eslint-disable-next-line
            const res = await fetch("http://localhost:5000/todos/card", {
                method: "DELETE",
                headers: jsonHead,
                body: JSON.stringify(body)
            });

            await res.json();
            const deleteCard = allCards.filter( c => c.card_id !== parseInt(cardId));
            setAllCards(deleteCard);

        } catch (error) {
            console.error(error.message);
        }
    }

    const createTodo = async(e) => {
        e.preventDefault();

        if(todoName === '') {
            return;
        }

        const jsonHead = new Headers();
        jsonHead.append('Content-type', 'application/json');
        jsonHead.append('token', localStorage.token);
        const cardId = mainCard.card_id;
        const body = {todoName, cardId};

        try {
            const res = await fetch("http://localhost:5000/todos/todo", {
                method: "POST",
                headers: jsonHead,
                body: JSON.stringify(body)
            });

            const parseRes = await res.json();
            //find the right card, then todo and update

            console.log(parseRes.rows[0]);
            //setMainTodos([...mainTodos, parseRes.rows[0]]);
            //setAllTodos([...allTodos, parseRes.rows[0]]);
            setTodoName('');
        } catch (error) {
            console.error(error.message);
        }
    }

    const deleteTodo = async(e) => {
        e.preventDefault();
        const jsonHead = new Headers();
        jsonHead.append('Content-type', 'application/json');
        jsonHead.append('token', localStorage.token);
        const cardId = mainCard.card_id;
        const todoId = e.target.parentElement.parentElement.id;
        const body = { cardId, todoId };
        try {
            // eslint-disable-next-line
            const res = await fetch("http://localhost:5000/todos/todo", {
                method: "DELETE",
                headers: jsonHead,
                body: JSON.stringify(body)
            });

            await res.json();
            const deleteTodo = mainTodos.filter(t => t.todo_id !== parseInt(todoId));
            setMainTodos(deleteTodo);
            const deleteAllTodo = allTodos.filter(t => t.todo_id !== parseInt(todoId));
            setAllTodos(deleteAllTodo);
        } catch (error) {
            console.error(error.message);
        }
    }

    const viewCard = (e) => {

        const cardIndex = allCards.findIndex(c => c.card_id === parseInt(e.currentTarget.parentElement.parentElement.id));
        setMainCard(allCards[cardIndex]);        
        //toggle if view card is true, if it is return main todos instead of cardlist
        setViewTodos(true);
    }

    const viewCardList = () => {
        setViewTodos(false)
    }

    return (
        <Fragment>
            {
                viewTodos ? (
                    <Fragment>
                        <div id="todo-top" onClick={viewCardList}>
                            {mainCard.card_name}
                        </div> 
                        <div id="todo-bottom">
                            { 
                                mainCard.todos.map( todo => {
                                    return <div key={todo.todo_id}>{todo.todo_title}</div>
                                })
                            }
                            <form onSubmit={createTodo}>
                                <input value={todoName} type="text" name="todo" placeholder="Todo title" onChange={e => setTodoName(e.target.value)}></input>
                                <button>Create Todo</button>
                            </form>
                        </div>
                    </Fragment>
                ) : (
                    <Fragment>
                        <div id="cards-top-wrap">
                            <span>Hello, {username}</span>
                        </div>
                        <div id="cards-bot-wrap">
                            <CardList allCards={allCards} setAllCards={setAllCards} deleteCard={deleteCard} viewCard={viewCard} />
                        </div>
                        <div id="cards-footer">
                            <form onSubmit={createCard}>
                                    <input value={cardName} type="text" name="card" placeholder="Card title" onChange={e => setCardName(e.target.value)}></input>
                                    <button>Create Card</button>
                            </form>
                            <button onClick={logout}>Logout</button>
                        </div>
                    </Fragment>
                )
            }
        </Fragment>
    );
}

export default Todos;
